import db from './db/db'
import { Database } from './configs'
import * as Koa from 'koa'
import * as Router from 'koa-router'
import configs from './configs'

import { menuRouter } from './routes'

// const Koa = require('koa');
const app = new Koa()
const router = new Router()

const PORT = configs.Server.port

router.get('/', (ctx, next) => {
	ctx.body = { message: 'Hello World!' }
	next()
})

router.get('/test', async (ctx) => {
	try {
		const data = await db.select('*').from(`${Database.schema}.Menu`).limit(10).offset(0)
		ctx.body = { data: data }
	} catch (err) {
		ctx.body = { message: err }
	}
})

app.use(router.routes()).use(router.allowedMethods())
app.use(menuRouter.routes()).use(menuRouter.allowedMethods())

const server = app.listen(PORT, () => {
	console.log(`Server listening on port: ${PORT}`)
})

export default server

import * as dotenv from 'dotenv'

// Set the NODE_ENV to 'development' by default
process.env.NODE_ENV = process.env.NODE_ENV || 'development'
const nodeEnv = process.env.NODE_ENV

const envFound = dotenv.config({ path: `${__dirname}/../../.env` })
if (envFound.error) {
	// Crash the whole process
	throw new Error("⚠️  Couldn't find .env file  ⚠️")
}

export namespace Database {
	export const schema =
		nodeEnv === 'development'
			? process.env.DB_SCHEMA_DEV!
			: nodeEnv === 'test'
			? process.env.DB_SCHEMA_TEST!
			: process.env.DB_SCHEMA_PROD!
	export const url = process.env.DB_URL
	export const host = process.env.DB_HOST
	export const user = process.env.DB_USER
	export const database = process.env.DATABASE
	export const password = process.env.DB_PASSWORD
	export const minPoolSize = parseInt(process.env.DB_MIN_POOL_SIZE!)
	export const maxPoolSize = parseInt(process.env.DB_MAX_POOL_SIZE!)
	export const port = parseInt(process.env.DB_PORT!, 10)
}

export namespace Server {
	export const port =
		process.env.NODE_ENV === 'docker' ? 1337 : parseInt(process.env.PORT || '3001', 10)
	export const isDev = process.env.NODE_ENV === 'development'
}

export namespace KnexCfg {
	export const schema = 'resto'

	export const config = {
		client: 'postgresql',
		connection: {
			host: Database.host,
			database: Database.database,
			user: Database.user,
			password: Database.password,
			port: Database.port,
			schema: Database.schema
		},
		pool: {
			min: Database.minPoolSize,
			max: Database.maxPoolSize
		},
		migrations: {
			tableName: 'KnexMigrations'
		},
		seeds: {
			directory: './seeds'
		}
	}
}

export namespace ApiVersion {
	export const v1 = '/api/v1'
}

export default { Database, Server, KnexCfg, ApiVersion }

import { Context } from 'koa'

export default class MenuController {
	public static getAll(ctx: Context) {
		ctx.body = { message: 'All Menu' }
	}
}

import {
	IRawMenu,
	IRawRestaurant,
	IRestaurant,
	IWorkingHours,
	IMenu,
	IRestaurantMenu,
	IRestoWithWorkingHours,
	RestaurantName,
	RestaurantId,
	MenuName,
	MenuId
} from '../../types/restaurant.types'

import { cvtDayOfWeekToInt, cvtTimeFromAMPM, cvtTimeToMinutes } from '../time/timeProcess'

/**
 *
 * @param restoId : Restaurant id on which the working hours belong to
 * @param day : Day between Monday-Sunday
 * @param time : Array of time containing opening hours & closing hours of the resto. Ex. const time1 = ['11:45AM', '4:45PM']
 * @returns : Object of IWorkingHours containing restaurantId, dayOfWeek on integer, also opening-closing hours in minutes pas miidnight
 */
function normalizeWorkingHoursData(restoId: number, day: string, time: string[]): IWorkingHours {
	const isValid =
		time.length === 2 &&
		(time[0].toLowerCase().includes('am') || time[0].toLowerCase().includes('pm'))
	if (!isValid) {
		throw new Error(
			'Parameter time should contain two elements of opening and closing hour in HH:MM AM/PM format'
		)
	}

	const opHour = time[0].toLowerCase()
	const clHour = time[1].toLowerCase()

	const normOH = cvtTimeFromAMPM(opHour)
	const normCH = cvtTimeFromAMPM(clHour)

	const finalDay = cvtDayOfWeekToInt(day)

	let minutesOH = cvtTimeToMinutes(normOH)
	let minutesCH = cvtTimeToMinutes(normCH)

	if (minutesOH > minutesCH) {
		// For now, assume that the times are somehow got switched
		const tempOH = minutesOH
		minutesOH = minutesCH
		minutesCH = tempOH
	}

	const wh: IWorkingHours = {
		restaurantId: restoId,
		dayOfWeek: finalDay,
		openingHour: minutesOH,
		closingHour: minutesCH
	}

	return wh
}

/**
 *
 * @param restoId (number) Restaurant id based on the restaurant data.
 * @param wh Working hours of the restaurant.Example input is
 * "Mon - Tues, Thurs, Sat 6:15 am - 3:15 am / Weds 9 am - 6:15 pm / Fri 5:15 am - 9:30 pm / Sun 4:45 pm - 5:45 pm"
 * @returns
 */
function extractWorkingHoursData(restoId: number, wh: string): IWorkingHours[] {
	const workingHours: IWorkingHours[] = []

	wh.split('/').forEach((dat) => {
		const day = [...dat.matchAll(/[A-Z][a-z]+/g)].map((i) => i[0])
		const time = [...dat.matchAll(/\d+(:\d+)?\s*(am|pm)?/g)].map((i) => i[0])

		day.forEach((d) => {
			const normalized = normalizeWorkingHoursData(restoId, d, time)
			workingHours.push(normalized)
		})
	})

	return workingHours
}

/**
 * @description Function to extract and transform raw restaurant data
 *
 * @param data : Array of restaurant data object containing restaurantName, cashBalance, openingHours, and array of menu
 * @returns : Object containing transformed restaurants and working hours data
 */
function etRestaurantData(data: IRawRestaurant[]): IRestoWithWorkingHours {
	const restoMap = new Map<RestaurantName, RestaurantId>()

	let whData: IWorkingHours[] = []
	const restoData: IRestaurant[] = []

	data.forEach((resto, idx) => {
		const restoName = resto.restaurantName.trim()
		if (!restoMap.has(restoName)) {
			const restoId = idx + 1

			restoData.push({
				restaurantId: restoId,
				restaurantName: restoName,
				cashBalance: resto.cashBalance
			})

			const wh = extractWorkingHoursData(restoId, resto.openingHours)
			whData = [...whData, ...wh]

			restoMap.set(restoName, restoId)
		}
	})

	const transformed: IRestoWithWorkingHours = {
		restoData: restoData,
		workingHours: whData
	}

	return transformed
}

/**
 * @description : Function to get distinct menu in all the restaurants
 *
 * @param rawResto : Restaurant raw data containing restaurantName, cashBalance, openingHours, and array of menu
 * @returns : Array of menu object containing menuId and dishname
 */
function etAllMenuData(rawResto: IRawRestaurant[]): IMenu[] {
	const menuMap = new Map<MenuName, MenuId>()

	const menu: IMenu[] = []
	let menuId = 0

	rawResto.forEach((dat) => {
		dat.menu.forEach((item) => {
			const dishname = item.dishName.trim()
			if (!menuMap.has(dishname.toLowerCase())) {
				menuId += 1
				menu.push({
					menuId: menuId,
					dishname: dishname.split('-')[0]
				})
				menuMap.set(dishname.toLowerCase(), menuId)
			}
		})
	})

	return menu
}

/**
 * @description Function to get menu item that belongs to each restaurant
 *
 * @param menu : Array of transformed menu data
 * @param resto : Array of transformed restaurant data
 * @param rawResto : Array of raw restaurant data
 * @returns : Array of menu item mapped to each restaurant and menu list
 */
function etRestaurantMenuData(
	menu: IMenu[],
	resto: IRestaurant[],
	rawResto: IRawRestaurant[]
): IRestaurantMenu[] {
	let id = 0
	const restoMenu: IRestaurantMenu[] = []

	rawResto.forEach((dat) => {
		dat.menu.forEach((men) => {
			id += 1
			const menuItem = menu.filter(
				(x) => x.dishname.toLowerCase().trim() === men.dishName.toLowerCase().trim()
			)
			const restoData = resto.filter(
				(x) => x.restaurantName.toLowerCase().trim() === dat.restaurantName.toLowerCase().trim()
			)

			if (menuItem.length > 0 && restoData.length > 0) {
				restoMenu.push({
					restaurantMenuId: id,
					restaurantId: restoData[0].restaurantId,
					menuId: menuItem[0].menuId,
					price: men.price
				})
			}
		})
	})

	return restoMenu
}

export {
	etAllMenuData,
	etRestaurantData,
	etRestaurantMenuData,
	extractWorkingHoursData,
	normalizeWorkingHoursData
}

import { errMessages } from '../../constants/errorMessages'

/**
 * @description Function to convert time of 12h format to 24h format
 *
 *
 * @param time (string) : Time in 12h AM/PM such as 1PM, 3:02AM, or 12:51AM
 * @returns finalTime (string) : Converted time in 24h format such as 13:12, 01:30
 */
function cvtTimeFromAMPM(time: string): string {
	const validData = time.toLowerCase().includes('am') || time.toLowerCase().includes('pm')
	if (!validData) {
		throw new Error('Error! Parameter time should be followed by AM/am or PM/pm identification.')
	}

	// Get time Hours, Minutes, and AM/PM
	let hourData = [...time.matchAll(/\d*(:)?(\d+)/g)].map((i) => i[0])[0]
	const hourUnit = [...time.toLowerCase().matchAll(/(am|pm)+/g)].map((i) => i[0])[0]

	const splitTime = hourData.split(':')
	const tempValueLen = splitTime.length
	const tempHour = parseInt(splitTime[0])
	const tempMinute = tempValueLen === 2 ? parseInt(splitTime[1]) : 0

	if (tempHour < 0 || tempHour > 12 || tempMinute < 0 || tempMinute > 59) {
		throw new Error(
			'Error. Input hour should be in range of 0-12 and minutes should be in range of 0-59'
		)
	}

	let finalTime = ''

	if (hourUnit === 'am') {
		if (tempHour < 10 && tempMinute < 10) {
			finalTime = `0${tempHour}:0${tempMinute}`
		} else if (tempHour < 10 && tempMinute >= 10) {
			finalTime = `0${tempHour}:${tempMinute}`
		} else if (tempHour >= 10 && tempHour !== 12 && tempMinute < 10) {
			finalTime = `${tempHour}:0${tempMinute}`
		} else if (tempHour >= 10 && tempHour !== 12 && tempMinute >= 10) {
			finalTime = `${tempHour}:${tempMinute}`
		} else if (tempHour === 12 && tempMinute < 10) {
			finalTime = `00:0${tempMinute}`
		} else {
			finalTime = `00:${tempMinute}`
		}
	} else {
		const pmHour = tempHour === 12 ? tempHour : `${tempHour + 12}`
		if (tempMinute < 10) {
			finalTime = `${pmHour}:0${tempMinute}`
		} else {
			finalTime = `${pmHour}:${tempMinute}`
		}
	}

	return finalTime
}

/**
 * @description
 * Function to convert HH:MM (24-h) time
 * into total minutes after midnight
 *
 * @param time (string): time in 24-hours base with HH:MM format
 * @returns timeMinutes (number) : time in minutes past midnight format
 */
function cvtTimeToMinutes(time: string): number {
	// Check Data
	const check = time.includes(':') && time.length > 2
	if (!check) {
		throw new Error(`${errMessages.ErrConvertTimeToMinutes}`)
	}

	// Split time to hours and minutes
	const splitTime = time.split(':')
	const hours = parseInt(splitTime[0])
	const minutes = parseInt(splitTime[1])

	// Calculate total minutes past midnight
	const timeMinutes = hours * 60 + minutes

	// return
	return timeMinutes
}

/**
 * @description
 * Function to convert time from minutes past
 * midnight format into 24-h HH:MM
 *
 * @param timeMinutes (number)
 * @returns hhmm : Time in 24-h HH:MM format
 */
function cvtMinutesToTime(timeMinutes: number): string {
	if (timeMinutes >= 1440) {
		throw new Error(errMessages.ErrConvertMinutesToTime)
	}

	const hours = Math.floor(timeMinutes / 60)
	const minutes = timeMinutes % 60

	const hoursTime = hours < 10 ? `0${hours}` : `${hours}`
	const minutesTime = minutes === 0 ? '00' : `${minutes}`

	const hhmm = `${hoursTime}:${minutesTime}`
	return hhmm
}

function cvtDayOfWeekToInt(day: string): number {
	const dayOfWeek = [
		{ id: 1, day: 'mon' },
		{ id: 2, day: 'tue' },
		{ id: 3, day: 'wed' },
		{ id: 4, day: 'thu' },
		{ id: 5, day: 'fri' },
		{ id: 6, day: 'sat' },
		{ id: 7, day: 'sun' }
	]

	const validData = dayOfWeek.findIndex((el) => day.toLowerCase().includes(el.day))
	if (validData === -1) {
		throw new Error(
			'Invalid day data! Day value should be written in English between Sunday & Monday'
		)
	}

	const intDay = dayOfWeek[validData].id

	return intDay
}

export { cvtTimeToMinutes, cvtMinutesToTime, cvtTimeFromAMPM, cvtDayOfWeekToInt }

import * as fs from 'fs'

/**
 * @description Function for reading json file in the given filepath
 *
 * @param filePath (string) : directory + filename
 * @returns Promise (TReturn) : Return promised data with TReturn type
 */
function readJsonFile<TReturn>(filePath: string): Promise<TReturn> {
	return new Promise((resolve) => {
		if (!filePath.includes('.json')) {
			throw new Error('Error! Can only read JSON file in .json')
		} else {
			fs.readFile(filePath, 'utf8', (error, jsonString) => {
				if (error != null) {
					throw new Error('Error while reading the file!')
				}

				try {
					const returnData: TReturn = JSON.parse(jsonString)
					resolve(returnData)
				} catch (err) {
					throw new Error('Error parsing JSON data from file!')
				}
			})
		}
	})
}

/**
 * @description Function to write json data into an output file directory
 *
 *
 * @param filepath (string): Absolute filepath directory
 * @param filename (string): Filename of the output file
 * @param jsonString (string): Stringified json data
 * @returns Promise (string) : Promise with success message if success
 */

function writeJsonFile(filepath: string, filename: string, jsonString: string): Promise<string> {
	return new Promise((resolve) => {
		if (!filename.includes('.json')) {
			throw new Error('Error! Can only save json data into .json file')
		} else {
			const file = filepath + filename
			fs.writeFile(file, jsonString, 'utf8', function (err) {
				if (err) {
					throw new Error('An error occured while writing JSON Object to File.')
				}
				resolve('Success writing the file!')
			})
		}
	})
}

export { readJsonFile, writeJsonFile }
